package vn.edu.iuh.fit.config;import org.springframework.boot.CommandLineRunner;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;import vn.edu.iuh.fit.entity.Tour;import vn.edu.iuh.fit.repositories.TourRepository;import java.math.BigDecimal;import java.util.Date;import java.util.UUID;@Configurationpublic class DataLoader {    @Bean    public CommandLineRunner loadData(TourRepository tourRepository) {        return args -> {            tourRepository.save(new Tour(                    UUID.randomUUID(),                    "Tour Paris",                    "Chuyến du lịch tuyệt vời tại Paris, nơi có những danh lam thắng cảnh nổi tiếng và văn hóa phong phú.",                    7,                    new Date(),                    new Date(),                    new BigDecimal("1500.00"),                    20,                    null,                    null,                    null            ));            tourRepository.save(new Tour(                    UUID.randomUUID(),                    "Tour London",                    "Khám phá vẻ đẹp của London với các điểm đến nổi bật như Big Ben và Cung điện Buckingham.",                    5,                    new Date(),                    new Date(),                    new BigDecimal("1200.00"),                    15,                    null,                    null,                    null            ));            tourRepository.save(new Tour(                    UUID.randomUUID(),                    "Tour New York",                    "Trải nghiệm thành phố sôi động New York với các địa điểm như Times Square và Central Park.",                    6,                    new Date(),                    new Date(),                    new BigDecimal("1800.00"),                    25,                    null,                    null,                    null            ));        };    }}