package vn.edu.iuh.fit.controller;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import vn.edu.iuh.fit.dto.MonthlyReviewStatistics;import vn.edu.iuh.fit.dto.TourReviewStatistics;import vn.edu.iuh.fit.dto.UserReviewStatistics;import vn.edu.iuh.fit.service.ReviewService;import java.util.List;@RestController@RequestMapping("/api/reviews")public class ReviewController {    @Autowired    private ReviewService reviewService;    @GetMapping("/statistics-tour-review")    public List<TourReviewStatistics> getAllTourReviewStatistics() {        return reviewService.getAllTourReviewStatistics();    }    @GetMapping("/monthly-statistics")    public List<MonthlyReviewStatistics> getMonthlyReviewStatistics() {        return reviewService.getMonthlyReviewStatistics();    }    @GetMapping("/user-statistics")    public List<UserReviewStatistics> getUserReviewStatistics() {        return reviewService.getUserReviewStatistics();    }}