package vn.edu.iuh.fit.controller;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.http.HttpStatus;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.*;import vn.edu.iuh.fit.entity.Tour;import vn.edu.iuh.fit.service.TourService;import java.util.List;import java.util.UUID;@RestController@RequestMapping("/api/tours")public class TourController {    @Autowired    private TourService tourService;    @PostMapping    public ResponseEntity<Tour> createTour(@RequestBody Tour tour) {        Tour createdTour = tourService.create(tour);        return new ResponseEntity<>(createdTour, HttpStatus.CREATED);    }    @GetMapping("/{id}")    public ResponseEntity<Tour> getTourById(@PathVariable("id") UUID id) {        Tour tour = tourService.getById(id);        return tour != null                ? new ResponseEntity<>(tour, HttpStatus.OK)                : new ResponseEntity<>(HttpStatus.NOT_FOUND);    }    @GetMapping    public ResponseEntity<List<Tour>> getAllTours() {        List<Tour> tours = tourService.getAll();        return new ResponseEntity<>(tours, HttpStatus.OK);    }    @PutMapping("/{id}")    public ResponseEntity<Tour> updateTour(@PathVariable("id") UUID id, @RequestBody Tour tour) {        if (!id.equals(tour.getTourId())) {            return new ResponseEntity<>(HttpStatus.BAD_REQUEST);        }        tourService.update(tour);        return new ResponseEntity<>(tour, HttpStatus.OK);    }    @DeleteMapping("/{id}")    public ResponseEntity<Void> deleteTour(@PathVariable("id") UUID id) {        tourService.delete(id);        return new ResponseEntity<>(HttpStatus.NO_CONTENT);    }}