package vn.edu.iuh.fit.controller;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.PathVariable;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import vn.edu.iuh.fit.service.UserService;@RestController@RequestMapping("/api/users")public class UserController {    @Autowired    private UserService userService;    @GetMapping("/exists/{email}")    public ResponseEntity<Boolean> checkAccountExists(@PathVariable String email) {        boolean exists = userService.existsByEmail(email);        return ResponseEntity.ok(exists);    }}