package vn.edu.iuh.fit.dto;import lombok.Data;import vn.edu.iuh.fit.entity.Image;import java.math.BigDecimal;import java.util.List;import java.util.Objects;import java.util.UUID;@Datapublic class TourDetailDTO {    private UUID tourId;    private String tourName;    private String tourDescription;    private BigDecimal price;    private Integer duration;    private List<DepartureDTO> departures;    private List<DestinationDTO> destinations;    private List<ReviewDTO> reviews;    private List<ImageDTO> images;    @Override    public boolean equals(Object o) {        if (this == o) return true;        if (!(o instanceof TourDetailDTO that)) return false;        return Objects.equals(getTourId(), that.getTourId()) && Objects.equals(getTourName(), that.getTourName());    }    @Override    public int hashCode() {        return Objects.hash(getTourId(), getTourName());    }}