package vn.edu.iuh.fit.dto.respone;import lombok.Data;import java.util.List;@Datapublic class ItineraryResponse {    private long userId;    private List<TourItinerary> itinerary;    @Data    public static class TourItinerary {        private long tourId;        private String tourName;        private List<DepartureInfo> departures;        private List<DestinationInfo> destinations;    }    @Data    public static class DepartureInfo {        private long departureId;        private String startDate;        private String endDate;    }    @Data    public static class DestinationInfo {        private long destinationId;        private String name;        private Integer duration;        private Integer sequenceOrder;    }}