package vn.edu.iuh.fit.entity;import jakarta.persistence.*;import lombok.*;import vn.edu.iuh.fit.enums.AccountRole;import java.util.UUID;@Entity@Table(name = "accounts")@Getter@Setter@NoArgsConstructor@AllArgsConstructorpublic class Account{    @Id    private long userId;    @OneToOne    @MapsId    @JoinColumn(name = "user_id", referencedColumnName = "userId", insertable = false, updatable = false)    private User user;    private String username;    private String password;    @Enumerated(EnumType.STRING)    private AccountRole role;    private boolean isActive;}