package vn.edu.iuh.fit.entity;import com.fasterxml.jackson.annotation.JsonBackReference;import com.fasterxml.jackson.annotation.JsonIgnore;import jakarta.persistence.*;import lombok.*;import java.util.UUID;@Entity@Table(name = "addresses")@Getter@Setter@NoArgsConstructor@AllArgsConstructorpublic class Address{    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private Long addressId;    private String address;    @ManyToOne    @JoinColumn(name = "user_id", referencedColumnName = "userId", nullable = false)    @JsonBackReference    @JsonIgnore    private User user;}