package vn.edu.iuh.fit.entity;import jakarta.persistence.*;import lombok.Getter;import lombok.Setter;import vn.edu.iuh.fit.enums.BookingStatus;import java.math.BigDecimal;import java.time.LocalDate;import java.util.Set;import java.util.UUID;@Getter@Setter@Entity@Table(name = "booking")public class Booking {    @Id    @GeneratedValue()    private UUID bookingId;    @ManyToOne    @JoinColumn(name = "user_id")    private User user;    @ManyToOne    @JoinColumn(name = "departure_id")    private Departure departure;    private LocalDate bookingDate;    private Integer numberOfParticipants;    private BigDecimal totalPrice;    @Enumerated(EnumType.STRING)    @Column(nullable = false)    private BookingStatus status;}