package vn.edu.iuh.fit.entity;import com.fasterxml.jackson.annotation.JsonIgnoreProperties;import jakarta.persistence.*;import lombok.*;import vn.edu.iuh.fit.enums.BookingStatus;import java.math.BigDecimal;import java.time.LocalDate;import java.time.LocalDateTime;import java.util.Set;import java.util.UUID;@Getter@Setter@Entity@Table(name = "booking")@NoArgsConstructor@AllArgsConstructor@Builderpublic class Booking {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private long bookingId;    @ManyToOne    @JoinColumn(name = "user_id")    @JsonIgnoreProperties({"email", "phoneNumber", "addresses", "experienceYear"})    private User user;    @ManyToOne    @JoinColumn(name = "departure_id")    private Departure departure;    private LocalDateTime bookingDate;    private String participants;    private boolean isActive;    @OneToMany(mappedBy = "booking", cascade = CascadeType.ALL, fetch = FetchType.LAZY)    private Set<Payment> payments;}