package vn.edu.iuh.fit.entity;import com.fasterxml.jackson.annotation.JsonIgnore;import jakarta.persistence.*;import lombok.*;import java.math.BigDecimal;import java.time.LocalDate;import java.time.LocalDateTime;import java.util.*;@Entity@Table(name = "departures")@Getter@Setter@NoArgsConstructor@AllArgsConstructor@Builderpublic class Departure{    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private long departureId;    @ManyToOne    @JoinColumn(name = "tour_id")    private Tour tour;    private LocalDateTime startDate;    private LocalDateTime endDate;    private Integer availableSeats;    private Integer maxParticipants;    @Column(name = "is_active")    private boolean isActive;    @OneToMany(mappedBy = "departure")    @JsonIgnore    private Set<Booking> bookings;    @OneToMany(mappedBy = "departure", fetch = FetchType.LAZY)    @JsonIgnore    private List<TourPricing> tourPricing;    @Override    public boolean equals(Object o) {        if (this == o) return true;        if (!(o instanceof Departure departure)) return false;        return getDepartureId() == departure.getDepartureId();    }    @Override    public int hashCode() {        return Objects.hash(getDepartureId());    }}