package vn.edu.iuh.fit.entity;import jakarta.persistence.*;import lombok.*;import java.math.BigDecimal;import java.time.LocalDate;import java.util.Set;import java.util.UUID;@Entity@Table(name = "departures")@Getter@Setter@NoArgsConstructor@AllArgsConstructor@Datapublic class Departure {    @Id    @GeneratedValue()    private UUID departureId;    @ManyToOne    @JoinColumn(name = "tour_id")    private Tour tour;    private LocalDate startDate;    private LocalDate endDate;    private Integer availableSeats;    private Integer maxParticipants;    @OneToMany(mappedBy = "departure")    private Set<Booking> bookings;    @OneToMany(mappedBy = "departure")    private Set<TourPricing> tourPricings;}