package vn.edu.iuh.fit.entity;import com.fasterxml.jackson.annotation.JsonIgnore;import jakarta.persistence.*;import lombok.*;import java.math.BigDecimal;import java.time.LocalDate;import java.time.LocalDateTime;import java.util.Arrays;import java.util.List;import java.util.Set;import java.util.UUID;@Entity@Table(name = "departures")@Getter@Setter@NoArgsConstructor@AllArgsConstructor@Builderpublic class Departure {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private long departureId;    @ManyToOne    @JoinColumn(name = "tour_id")    private Tour tour;    private LocalDateTime startDate;    private LocalDateTime endDate;    private Integer availableSeats;    private Integer maxParticipants;    @OneToMany(mappedBy = "departure")    @JsonIgnore    private Set<Booking> bookings;    @OneToMany(mappedBy = "departure", fetch = FetchType.LAZY)    @JsonIgnore    private List<TourPricing> tourPricing;}