package vn.edu.iuh.fit.entity;import jakarta.persistence.*;import lombok.*;import java.util.Set;import java.util.UUID;@Entity@Table(name = "destinations")@Getter@Setter@NoArgsConstructor@AllArgsConstructor@Datapublic class Destination {    @Id    @GeneratedValue()    private long destinationId;    private String name;    @Column(length = 1000)    private String description;    private String province;    @OneToMany(mappedBy = "destination")    private Set<TourDestination> tourDestinations;}