package vn.edu.iuh.fit.entity;import com.fasterxml.jackson.annotation.JsonIgnore;import jakarta.persistence.*;import lombok.*;import java.util.List;import java.util.Objects;import java.util.Set;@Entity@Table(name = "destinations")@Getter@Setter@NoArgsConstructor@AllArgsConstructor@Builderpublic class Destination{    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private long destinationId;    private String name;    @Column(length = 1000)    private String description;    private String province;    @OneToMany(mappedBy = "destination")    @JsonIgnore    private List<TourDestination> tourDestinations;    @Override    public boolean equals(Object o) {        if (this == o) return true;        if (!(o instanceof Destination that)) return false;        return getDestinationId() == that.getDestinationId();    }    @Override    public int hashCode() {        return Objects.hash(getDestinationId());    }    @OneToMany(mappedBy = "destination", cascade = CascadeType.ALL, fetch = FetchType.LAZY)    @JsonIgnore    private Set<Image> images;}