package vn.edu.iuh.fit.entity;import jakarta.persistence.*;import lombok.*;import java.time.LocalDateTime;@Entity@Table(name = "discounts")@Getter@Setter@NoArgsConstructor@AllArgsConstructorpublic class Discount{    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private Long id;    private String discountCode;    private Long discountAmount;    private LocalDateTime startDate;    private LocalDateTime endDate;    private Integer countUse;    @ManyToOne    @JoinColumn(name = "tour_id", referencedColumnName = "tourId", nullable = false)    private Tour tour;}