package vn.edu.iuh.fit.entity;import com.fasterxml.jackson.annotation.JsonFormat;import jakarta.persistence.*;import lombok.*;import java.time.LocalDateTime;@Entity@Table(name = "discounts")@Getter@Setter@NoArgsConstructor@AllArgsConstructorpublic class Discount{    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private Long id;    private String discountCode;    private Long discountAmount;    @JsonFormat(shape = JsonFormat.Shape.STRING, pattern = "yyyy-MM-dd'T'HH:mm:ss")    private LocalDateTime startDate;    @JsonFormat(shape = JsonFormat.Shape.STRING, pattern = "yyyy-MM-dd'T'HH:mm:ss")    private LocalDateTime endDate;    private Integer countUse;    @ManyToOne    @JoinColumn(name = "tour_id", referencedColumnName = "tourId", nullable = true)    private Tour tour;}