package vn.edu.iuh.fit.entity;import jakarta.persistence.*;import lombok.*;import java.time.LocalDateTime;import java.util.Objects;@Entity@Table(name = "favorite_tours")@Getter@Setter@NoArgsConstructor@AllArgsConstructorpublic class FavoriteTour {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private Long id;    @ManyToOne    @JoinColumn(name = "user_id", nullable = false)    private User user;    @ManyToOne    @JoinColumn(name = "tour_id", nullable = false)    private Tour tour;    private LocalDateTime addedDate;    @Override    public boolean equals(Object o) {        if (this == o) return true;        if (!(o instanceof FavoriteTour favoriteTour)) return false;        return Objects.equals(getId(), favoriteTour.getId());    }    @Override    public int hashCode() {        return Objects.hash(getId());    }}