package vn.edu.iuh.fit.entity;import com.fasterxml.jackson.annotation.JsonBackReference;import com.fasterxml.jackson.annotation.JsonIgnore;import jakarta.persistence.*;import lombok.*;import java.util.UUID;@Entity@Table(name = "images")@Getter@Setter@NoArgsConstructor@AllArgsConstructorpublic class Image{    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private long imageId;    private String imageUrl;    @ManyToOne    @JoinColumn(name = "tour_id", nullable = true)    @JsonIgnore    private Tour tour;    @ManyToOne    @JoinColumn(name = "destination_id", nullable = true)    @JsonIgnore    private Destination destination;}