package vn.edu.iuh.fit.entity;import com.fasterxml.jackson.annotation.JsonIgnore;import jakarta.persistence.*;import lombok.*;import vn.edu.iuh.fit.enums.PaymentMethod;import java.time.LocalDateTime;@Entity@Table(name = "payments")@Getter@Setter@NoArgsConstructor@AllArgsConstructorpublic class Payment {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private Long paymentId;    private Long amount;    private LocalDateTime paymentDate;    @Enumerated(EnumType.STRING)    private PaymentMethod paymentMethod;    @ManyToOne    @JoinColumn(name = "booking_id", referencedColumnName = "bookingId", nullable = false)    private Booking booking;}