package vn.edu.iuh.fit.entity;import jakarta.persistence.*;import lombok.*;import java.time.LocalDate;@Getter@Setter@Entity@Table(name = "review")@NoArgsConstructor@AllArgsConstructor@Datapublic class Review {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private Long reviewId;    @ManyToOne    @JoinColumn(name = "user_id")    private User user;    @ManyToOne    @JoinColumn(name = "tour_id")    private Tour tour;    private Integer rating;    @Column(length = 1000)    private String comment;    private LocalDate reviewDate;}