package vn.edu.iuh.fit.entity;import jakarta.persistence.*;import jakarta.validation.constraints.Max;import jakarta.validation.constraints.Min;import lombok.*;import java.time.LocalDate;import java.time.LocalDateTime;import java.util.UUID;@Getter@Setter@Entity@Table(name = "review")@NoArgsConstructor@AllArgsConstructor@Datapublic class Review {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private long reviewId;    @ManyToOne    @JoinColumn(name = "user_id")    private User user;    @ManyToOne    @JoinColumn(name = "tour_id")    private Tour tour;    @Min(1)    @Max(5)    private Integer rating;    @Column(length = 1000)    private String comment;    private LocalDateTime reviewDate;}