package vn.edu.iuh.fit.entity;import jakarta.persistence.*;import lombok.*;import java.io.Serializable;import java.time.Instant;@Entity@Table(name = "tokens")@Getter@Setter@NoArgsConstructor@AllArgsConstructor@Builderpublic class Token implements Serializable {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private Long id;    @Column(unique = true, length = 2048)    private String token;    @Column(unique = true)    private String refreshToken;    private Instant tokenExpiry;    private Instant refreshTokenExpiry;    private boolean revoked;    private boolean expired;    @ManyToOne    @JoinColumn(name = "user_id")    private User user;}