package vn.edu.iuh.fit.entity;import jakarta.persistence.*;import lombok.AllArgsConstructor;import lombok.Getter;import lombok.NoArgsConstructor;import lombok.Setter;import vn.edu.iuh.fit.pks.TourDestinationId;@Entity@Table(name = "tour_destinations")@Getter@Setter@NoArgsConstructor@AllArgsConstructor@IdClass(TourDestinationId.class)public class TourDestination {    @Id    @ManyToOne    @JoinColumn(name = "tour_id")    private Tour tour;    @Id    @ManyToOne    @JoinColumn(name = "destination_id")    private Destination destination;    private Integer duration;    private Integer sequenceOrder;}