package vn.edu.iuh.fit.entity;import com.fasterxml.jackson.annotation.JsonIgnore;import jakarta.persistence.*;import lombok.*;import vn.edu.iuh.fit.pks.TourDestinationId;@Entity@Table(name = "tour_destinations")@Getter@Setter@NoArgsConstructor@AllArgsConstructor@Builder@IdClass(TourDestinationId.class)public class TourDestination {    @Id    @ManyToOne    @JoinColumn(name = "tour_id")    @JsonIgnore    private Tour tour;    @Id    @ManyToOne    @JoinColumn(name = "destination_id")    private Destination destination;    private Integer duration;    private Integer sequenceOrder;}