package vn.edu.iuh.fit.entity;import com.fasterxml.jackson.annotation.JsonIgnore;import jakarta.persistence.*;import lombok.*;import vn.edu.iuh.fit.pks.TourDestinationId;import java.util.Objects;@Entity@Table(name = "tour_destinations")@Getter@Setter@NoArgsConstructor@AllArgsConstructor@Builder@IdClass(TourDestinationId.class)public class TourDestination{    @Id    @ManyToOne    @JoinColumn(name = "tour_id")    @JsonIgnore    private Tour tour;    @Id    @ManyToOne    @JoinColumn(name = "destination_id")    private Destination destination;    private Integer duration;    private Integer sequenceOrder;    @Override    public boolean equals(Object o) {        if (this == o) return true;        if (!(o instanceof TourDestination that)) return false;        return Objects.equals(getTour(), that.getTour()) && Objects.equals(getDestination(), that.getDestination());    }    @Override    public int hashCode() {        return Objects.hash(getTour(), getDestination());    }}