package vn.edu.iuh.fit.entity;import jakarta.persistence.*;import lombok.AllArgsConstructor;import lombok.Getter;import lombok.NoArgsConstructor;import lombok.Setter;import vn.edu.iuh.fit.enums.AssignStatus;import java.io.Serializable;import java.time.LocalDateTime;@Entity@Table(name = "tour_guide_assignments")@Getter@Setter@NoArgsConstructor@AllArgsConstructor@IdClass(TourGuideAssignmentId.class)public class TourGuideAssignment implements Serializable {    @Id    @Column(name = "departure_id")    private long departureId;    @Id    @Column(name = "guide_id")    private long guideId;    private LocalDateTime assignmentDate;    @Enumerated(EnumType.STRING)    private AssignStatus status;    @ManyToOne    @JoinColumn(name = "departure_id", insertable = false, updatable = false)    private Departure departure;    @ManyToOne    @JoinColumn(name = "guide_id", insertable = false, updatable = false)    private TourGuide tourGuide;}