package vn.edu.iuh.fit.entity;import jakarta.persistence.*;import lombok.*;import java.math.BigDecimal;@Entity@Table(name = "tour_pricing")@Getter@Setter@AllArgsConstructor@NoArgsConstructor@Datapublic class TourPricing {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private Long tourPricingId;    private BigDecimal price;    private Integer maxParticipants;    @ManyToOne    @JoinColumn(name = "departure_id")    private Departure departure;}