package vn.edu.iuh.fit.enums;public enum AccountRole {    ADMIN("Administrator"),    CUSTOMER("Customer"),    TOURGUIDE("Tour Guide"),    TOURPROVIDER("Tour Provider");    private final String description;    AccountRole(String description) {        this.description = description;    }    public String getDescription() {        return description;    }    @Override    public String toString() {        return this.name() + " (" + description + ")";    }}