package vn.edu.iuh.fit.enums;public enum TourType {    FAMILY("Famlily Tour"),    GROUP("Group Tour"),    DELETE("Delete Tour");    private final String description;    TourType(String description) {        this.description = description;    }    public String getDescription() {        return description;    }    @Override    public String toString() {        return this.name() + " (" + description + ")";    }}