package vn.edu.iuh.fit.enums;import lombok.Getter;@Getterpublic enum TourType {    FAMILY("Family Tour"),    GROUP("Group Tour"),    DELETE("Delete Tour"),    FAMILY_NORTHERN("Northern Family Tour"),    FAMILY_CENTRAL("Central Family Tour"),    FAMILY_SOUTHERN("Southern Family Tour"),    FAMILY_RELAXATION("Relaxation Family Tour"),    FAMILY_ADVENTURE("Adventure Family Tour"),    FAMILY_CULTURAL_AND_HISTORICAL("Cultural And History Family Tour"),    FAMILY_SPIRITUAL("Spiritual Family Tour"),    GROUP_NORTHERN("Northern Group Tour"),    GROUP_CENTRAL("Central Group Tour"),    GROUP_SOUTHERN("Southern Group Tour"),    GROUP_RELAXATION("Relaxation Group Tour"),    GROUP_ADVENTURE("Adventure Group Tour"),    GROUP_CULTURAL_AND_HISTORICAL("Cultural And History Group Tour"),    GROUP_SPIRITUAL("Spiritual Group Tour");    private final String description;    TourType(String description) {        this.description = description;    }    @Override    public String toString() {        return this.name() + " (" + description + ")";    }}