package vn.edu.iuh.fit.mailservice;import org.springframework.stereotype.Repository;import java.util.Map;import java.util.concurrent.ConcurrentHashMap;@Repositorypublic class VerificationCodeRepository {    private Map<String, String> codeStore = new ConcurrentHashMap<>();    public void saveCode(String email, String code) {        codeStore.put(email, code);    }    public String getCode(String email) {        return codeStore.get(email);    }    public void removeCode(String email) {        codeStore.remove(email);    }}