package vn.edu.iuh.fit.repositories;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.data.jpa.repository.Query;import org.springframework.data.repository.query.Param;import org.springframework.stereotype.Repository;import vn.edu.iuh.fit.entity.Booking;import vn.edu.iuh.fit.entity.Departure;import vn.edu.iuh.fit.entity.TourGuide;import java.util.List;import java.util.UUID;@Repositorypublic interface BookingRepository extends JpaRepository<Booking, String> {    List<Booking> findByDeparture(Departure departure);    @Query("SELECT b FROM Booking b WHERE YEAR(b.bookingDate) = :year")    List<Booking> findAllByYear(@Param("year") int year);    List<Booking> findByUser_UserId(long userId);    List<Booking> findByDepartureIn(List<Departure> departures);}