package vn.edu.iuh.fit.repositories;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.data.jpa.repository.Query;import org.springframework.stereotype.Repository;import vn.edu.iuh.fit.entity.Departure;import vn.edu.iuh.fit.entity.Tour;import java.time.LocalDateTime;import java.util.List;import java.util.UUID;@Repositorypublic interface DepartureRepository extends JpaRepository<Departure, Long> {    List<Departure> findAllByTour(Tour tour);    List<Departure> findAllByTourAndIsActive(Tour tour, boolean isActive);    @Query("SELECT d FROM Departure d WHERE DATE(d.startDate) = DATE(d.endDate) " +            "AND d.isActive=true AND d.availableSeats>0 ORDER BY d.startDate")    List<Departure> findAllInDay();}