package vn.edu.iuh.fit.repositories;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.data.jpa.repository.Query;import org.springframework.stereotype.Repository;import vn.edu.iuh.fit.entity.Token;import vn.edu.iuh.fit.entity.User;import java.util.List;import java.util.Optional;@Repositorypublic interface TokenRepository extends JpaRepository<Token, Long> {    @Query("""        select t from Token t inner join User u         on t.user.userId = u.userId         where u.userId = :userId and (t.expired = false or t.revoked = false)    """)    List<Token> findAllValidTokenByUser(Long userId);    Optional<Token> findByToken(String token);    Optional<Token> findByRefreshToken(String refreshToken);}