package vn.edu.iuh.fit.repositories;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.data.jpa.repository.Query;import org.springframework.data.repository.query.Param;import org.springframework.stereotype.Repository;import vn.edu.iuh.fit.entity.TourGuideAssignment;import vn.edu.iuh.fit.pks.TourGuideAssignmentId;import java.util.List;@Repositorypublic interface TourGuideAssignmentRepository extends JpaRepository<TourGuideAssignment, TourGuideAssignmentId> {    List<TourGuideAssignment> findByDeparture_DepartureId(long departureId);    @Query("SELECT a FROM TourGuideAssignment a " +            "JOIN FETCH a.tourGuide " +            "JOIN FETCH a.departure " +            "WHERE a.departure.departureId IN :departureIds")    List<TourGuideAssignment> findByDeparture_DepartureIdIn(@Param("departureIds") List<Long> departureIds);}