package vn.edu.iuh.fit.repositories;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.data.jpa.repository.Query;import org.springframework.data.repository.query.Param;import vn.edu.iuh.fit.entity.TourGuide;import vn.edu.iuh.fit.enums.AccountRole;import java.util.List;public interface TourGuideRepository extends JpaRepository<TourGuide, Long> {    @Query("SELECT tg FROM TourGuide tg JOIN tg.account a WHERE a.role = :role AND a.isActive = true")    List<TourGuide> findAllByRole(@Param("role") AccountRole role);}