package vn.edu.iuh.fit.repositories;import org.springframework.data.jpa.repository.EntityGraph;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.stereotype.Repository;import vn.edu.iuh.fit.entity.User;import vn.edu.iuh.fit.enums.AccountRole;import java.util.List;import java.util.Optional;import java.util.UUID;@Repositorypublic interface UserRepository extends JpaRepository<User, Long> {    Optional<User> findByEmail(String email);    boolean existsByEmail(String email);    @EntityGraph(attributePaths = {"addresses", "bookings", "reviews"})    Optional<User> findById(Long id);    List<User> findByAccount_RoleInAndAccount_IsActiveTrue(List<AccountRole> roles);}