package vn.edu.iuh.fit.service;import org.springframework.data.jpa.repository.JpaRepository;import java.util.List;public abstract class AbstractCrudService<T, ID> implements CrudService<T, ID> {    protected abstract JpaRepository<T, ID> getRepository();    @Override    public T create(T entity) {        return getRepository().save(entity);    }    @Override    public T getById(ID id) {        return getRepository().findById(id).orElse(null);    }    @Override    public List<T> getAll() {        return getRepository().findAll();    }    @Override    public void update(T entity) {        getRepository().save(entity);    }    @Override    public void delete(ID id) {        getRepository().deleteById(id);    }    public T saveOrUpdate(T entity) {        return getRepository().save(entity);    }}