package vn.edu.iuh.fit.service;import org.springframework.data.domain.Page;import org.springframework.transaction.annotation.Transactional;import java.util.List;public interface CrudService<T, ID> {    T create(T entity);    T getById(ID id);    List<T> getAll();    void update(T entity);    void delete(ID id);    Page<T> getPageList(int page, int size, String sortBy, String sortDirection);}