package vn.edu.iuh.fit.service.implement;import lombok.AllArgsConstructor;import org.modelmapper.ModelMapper;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.data.domain.Page;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.stereotype.Service;import vn.edu.iuh.fit.dto.BookingDTO;import vn.edu.iuh.fit.entity.Booking;import vn.edu.iuh.fit.repositories.BookingRepository;import vn.edu.iuh.fit.service.BookingService;import vn.edu.iuh.fit.service.DepartureService;@Service@AllArgsConstructorpublic class BookingServiceImpl extends AbstractCrudService<Booking, String> implements BookingService {    @Autowired    private BookingRepository bookingRepository;    private final ModelMapper modelMapper;    @Override    protected JpaRepository<Booking, String> getRepository() {        return bookingRepository;    }    @Override    public BookingDTO convertDTO(Booking booking) {        return modelMapper.map(booking, BookingDTO.class);    }}