package vn.edu.iuh.fit.service.implement;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.data.domain.Page;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.stereotype.Service;import vn.edu.iuh.fit.entity.Booking;import vn.edu.iuh.fit.repositories.BookingRepository;import vn.edu.iuh.fit.service.BookingService;@Servicepublic class BookingServiceImpl extends AbstractCrudService<Booking, Long> implements BookingService {    @Autowired    private BookingRepository bookingRepository;    @Override    protected JpaRepository<Booking, Long> getRepository() {        return bookingRepository;    }    @Override    public Page<Booking> getPageBooking(int page, int size, String sortBy, String sortDirection) {        return getPageList(page,size, sortBy, sortDirection);    }}