package vn.edu.iuh.fit.service.implement;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import vn.edu.iuh.fit.entity.Booking;import vn.edu.iuh.fit.repositories.BookingRepository;import vn.edu.iuh.fit.service.BookingService;import java.util.List;@Servicepublic class BookingServiceImpl implements BookingService {    @Autowired    private BookingRepository bookingRepository;    @Override    public Booking createBooking(Booking booking) {        return bookingRepository.save(booking);    }    @Override    public Booking getBookingById(Long id) {        return bookingRepository.findById(id).orElse(null);    }    @Override    public List<Booking> getAllBookings() {        return bookingRepository.findAll();    }    @Override    public void updateBooking(Booking booking) {        bookingRepository.save(booking);    }    @Override    public void deleteBooking(Long id) {        bookingRepository.deleteById(id);    }}