package vn.edu.iuh.fit.service.implement;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import vn.edu.iuh.fit.entity.Departure;import vn.edu.iuh.fit.repositories.BookingRepository;import vn.edu.iuh.fit.repositories.DepartureRepository;import vn.edu.iuh.fit.service.DepartureService;import java.util.List;@Servicepublic class DepartureServiceImpl implements DepartureService {    @Autowired    private DepartureRepository departureRepository;    @Override    public Departure createDeparture(Departure departure) {        return departureRepository.save(departure);    }    @Override    public Departure getDepartureById(Long id) {        return departureRepository.findById(id).orElse(null);    }    @Override    public List<Departure> getAllDepartures() {        return departureRepository.findAll();    }    @Override    public void updateDeparture(Departure departure) {        departureRepository.save(departure);    }    @Override    public void deleteDeparture(Long id) {        departureRepository.deleteById(id);    }}