package vn.edu.iuh.fit.service.implement;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.stereotype.Service;import vn.edu.iuh.fit.entity.Departure;import vn.edu.iuh.fit.entity.Tour;import vn.edu.iuh.fit.repositories.DepartureRepository;import vn.edu.iuh.fit.service.DepartureService;import java.util.List;@Servicepublic class DepartureServiceImpl extends AbstractCrudService<Departure, Long> implements DepartureService {    @Autowired    private DepartureRepository departureRepository;    @Override    protected JpaRepository<Departure, Long> getRepository() {        return departureRepository;    }    @Override    public List<Departure> getListDepartureByTour(Tour tour) {        return departureRepository.findAllByTour(tour);    }}