package vn.edu.iuh.fit.service.implement;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import vn.edu.iuh.fit.entity.Destination;import vn.edu.iuh.fit.repositories.DestinationRepository;import vn.edu.iuh.fit.service.DestinationService;import java.util.List;@Servicepublic class DestinationServiceImpl implements DestinationService {    @Autowired    private DestinationRepository destinationRepository;    @Override    public Destination createDestination(Destination destination) {     return  destinationRepository.save(destination);    }    @Override    public Destination getDestinationById(Long id) {        return destinationRepository.findById(id).get();    }    @Override    public List<Destination> getAllDestinations() {        return destinationRepository.findAll();    }    @Override    public void updateDestination(Destination destination) {        destinationRepository.save(destination);    }    @Override    public void deleteDestination(Long id) {        destinationRepository.delete(destinationRepository.findById(id).get());    }}