package vn.edu.iuh.fit.service.implement;import org.springframework.stereotype.Service;import vn.edu.iuh.fit.entity.Review;import vn.edu.iuh.fit.repositories.ReviewRepository;import vn.edu.iuh.fit.service.ReviewService;import java.util.List;@Servicepublic class ReviewServiceImpl implements ReviewService {    private ReviewRepository reviewRepository;    @Override    public Review createReview(Review review) {        return reviewRepository.save(review);    }    @Override    public Review getReviewById(Long id) {        return reviewRepository.findById(id).get();    }    @Override    public List<Review> getAllReviews() {        return reviewRepository.findAll();    }    @Override    public void updateReview(Review review) {        reviewRepository.save(review);    }    @Override    public void deleteReview(Long id) {    reviewRepository.delete(reviewRepository.findById(id).get());    }}