package vn.edu.iuh.fit.service.implement;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.stereotype.Service;import vn.edu.iuh.fit.entity.Tour;import vn.edu.iuh.fit.entity.TourDestination;import vn.edu.iuh.fit.repositories.TourDestinationRepository;import vn.edu.iuh.fit.service.TourDestinationService;import java.util.List;@Servicepublic class TourDestinationServiceImpl extends AbstractCrudService<TourDestination, Long> implements TourDestinationService {    @Autowired    private TourDestinationRepository tourDestinationRepository;    @Override    protected JpaRepository<TourDestination, Long> getRepository() {        return tourDestinationRepository;    }    @Override    public List<TourDestination> findAllByTour(Tour tour) {        return tourDestinationRepository.findAllByTour(tour);    }}