package vn.edu.iuh.fit.service.implement;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import vn.edu.iuh.fit.entity.TourPricing;import vn.edu.iuh.fit.repositories.TourDestinationRepository;import vn.edu.iuh.fit.repositories.TourPricingRepository;import vn.edu.iuh.fit.service.TourPricingService;import java.util.List;@Servicepublic class TourPricingServiceImpl implements TourPricingService {    @Autowired    private TourPricingRepository tourPricingRepository;    @Override    public TourPricing createTourPricing(TourPricing tourPricing) {        return tourPricingRepository.save(tourPricing);    }    @Override    public TourPricing getTourPricingById(Long id) {        return tourPricingRepository.findById(id).get();    }    @Override    public List<TourPricing> getAllTourPricings() {        return tourPricingRepository.findAll();    }    @Override    public void updateTourPricing(TourPricing tourPricing) {        tourPricingRepository.save(tourPricing);    }    @Override    public void deleteTourPricing(Long id) {        tourPricingRepository.delete(tourPricingRepository.findById(id).get());    }}