package vn.edu.iuh.fit.service.implement;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import vn.edu.iuh.fit.entity.User;import vn.edu.iuh.fit.repositories.UserRepository;import vn.edu.iuh.fit.service.UserService;import java.util.List;@Servicepublic class UserServiceImpl implements UserService {    @Autowired    private UserRepository userRepository;    @Override    public User createUser(User user) {        return userRepository.save(user);    }    @Override    public User getUserById(Long id) {        return userRepository.findById(id).get();    }    @Override    public List<User> getAllUsers() {        return userRepository.findAll();    }    @Override    public void updateUser(User user) {        userRepository.save(user);    }    @Override    public void deleteUser(Long id) {        userRepository.delete(userRepository.findById(id).get());    }}